import React from "react"
import Switch from "react-switch"

class ToggleSwitch extends React.Component {
    constructor() {
        super();
        this.state = { checked: true };
        this.handleChange = this.handleChange.bind(this);
      }
     
      handleChange(checked) {
        this.setState({ checked })
        if(checked){
          this.props.updateSelectedSwitch('guardian')
          this.props.history.push("/")
          this.props.history.push("/World")
          this.props.history.push("/Politics")
          this.props.history.push("/Business")
          this.props.history.push("/Technology")
          this.props.history.push("/Sports")
        }
        else{
          this.props.updateSelectedSwitch('NYtimes')
          this.props.history.push("/")
          this.props.history.push("/World")
          this.props.history.push("/Politics")
          this.props.history.push("/Business")
          this.props.history.push("/Technology")
          this.props.history.push("/Sports")
        }
      }
     
      render() {
        return (
          <label>
            <Switch
             onChange={this.handleChange} 
             checked={this.state.checked} 
             checkedIcon={false} 
             uncheckedIcon = {false}
             height = {20}
             width = {40}
             onColor= "#2693e6"/>
          </label>
        );
      }
    }

export default ToggleSwitch
