import React from "react"
import { withRouter } from "react-router-dom"

class DetailedArticle extends React.Component {
    constructor(){
        super()
    this.state = {
        Loading: true,
        article: []
    }
}
   
    componentDidMount() {
        let artId = window.location.search.substring(4,window.location.search.length);
        fetch("http://localhost:5000/guardiandetailed"+artId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })


    }
    
    render() {
        return(
            <h1>{this.state.article}</h1>
        )
    }
}


export default withRouter(DetailedArticle)
