import React from  "react"
import {EmailShareButton,
    FacebookShareButton, TwitterShareButton} from "react-share"
import {EmailIcon,FacebookIcon,TwitterIcon} from "react-share"
import Modal from 'react-bootstrap/Modal'

class Share extends React.Component {
    constructor(props){
        super(props)
    }
   
    render() {
    return (
        <Modal show={this.props.select} onHide={this.props.hide}>
        <Modal.Header closeButton>
            <Modal.Title>{this.props.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>Share via <FacebookShareButton><FacebookIcon></FacebookIcon></FacebookShareButton></Modal.Body>
        </Modal>
     
    );
  }
}
  


export default Share
