import React from "react"
import { withRouter } from "react-router-dom"

class DetailedArticle extends React.Component {
    constructor(){
        super()
    this.state = {
        Loading: true,
        article: []
    }
}

    componentDidMount() {
        fetch("http://localhost:5000/guardiandetailed"+ window.history.search).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })


    }
    
    render() {
        return(
            <h1> Hello! </h1>
        )
    }
}


export default withRouter(DetailedArticle)
