import React from "react"
import { withRouter } from "react-router-dom"

class DetailedArticle extends React.Component {
    constructor(){
        super()
    this.state = {
        Loading: true,
        article: []
    }
}
   
    componentDidMount() {
        if (localStorage.getItem('selected') === "guardian") {
        let artId = window.location.search.substring(4,window.location.search.length)
        fetch("http://localhost:5000/guardiandetailed?id="+artId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })
    }
       else{
        let artId = window.location.search.substring(4,window.location.search.length)
        fetch("http://localhost:5000/NYtimesdetailed?vals="+artId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })

       }
        

    }
    
    render() {
        console.log(this.state.article)
        return(
            <h1>hello!</h1>
        )
    }
}


export default withRouter(DetailedArticle)
