import React from "react"
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import AsyncSelect from 'react-select/async'
import {FaRegBookmark} from 'react-icons/fa'
import ToggleSwitch from './ToggleSwitch'
import {withRouter} from "react-router-dom"
import {Link, NavLink} from "react-router-dom"
import { NavItem } from "react-bootstrap"
import ReactTooltip from "react-tooltip"
class NavigationBar extends React.Component {
   constructor(){
     super()
     this.state = {
       selected : this.readLocalStorage()
     }
     this.handleCheck = this.handleCheck.bind(this);
     this.updateSelectedSwitch = this.updateSelectedSwitch.bind(this)
   }

   handleCheck(check){
     this.props.checkValue(check);
   }
   displaySwitch(){
     if(this.props.hide === false){
       return (
        <React.Fragment>
        <NavItem style={{marginRight:'1.5%',color:"whitesmoke"}}>NYTimes</NavItem>
        <ToggleSwitch updateSelectedSwitch={this.updateSelectedSwitch}/>
        <NavItem style={{marginLeft:'1.5%',color:"whitesmoke"}}>Guardian</NavItem>
        </React.Fragment>
       )
     }
   }
   

   readLocalStorage(){
      if (localStorage.getItem('selected')) {
        return localStorage.getItem('selected');
      } else {
        localStorage.setItem('selected', 'guardian');
        return localStorage.getItem('selected');
      }
  }

   updateSelectedSwitch(val){
     console.log(val)
     this.setState({selected: val})
     localStorage.setItem('selected',val)
    // this.handleCheck(val);
    }

    HandleBookmark() {
      this.props.history.push({pathname:"/BookMark"})
    }
  
    render(){
        return(
            <Navbar collapseOnSelect bg='dark' expand="lg" variant="dark" className="nav-name">
            <AsyncSelect className="nav-search"
              cacheOptions
              defaultOptions
              placeholder= "Enter keyword .."/>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto" defaultActiveKey="/">
                <Nav.Link as={NavLink} exact to="/" href="/" activeStyle={{color:"white"}} style={{color:"lightgray"}}>Home</Nav.Link>
                <Nav.Link as={NavLink} to="/World" href="/World" activeStyle={{color:"white"}} style={{color:"lightgray"}}>World</Nav.Link>
                <Nav.Link as={NavLink} to="/Politics" href="/Politics" activeStyle={{color:"white"}} style={{color:"lightgray"}}>Politics</Nav.Link>
                <Nav.Link as={NavLink} to="/Business" href="/Business" activeStyle={{color:"white"}} style={{color:"lightgray"}}>Business</Nav.Link>
                <Nav.Link as={NavLink} to="/Technology" href="/Technology" activeStyle={{color:"white"}} style={{color:"lightgray"}}>Technology</Nav.Link>
                <Nav.Link as={NavLink} to="/Sports" href="/Sports" activeStyle={{color:"white"}} style={{color:"lightgray"}}>Sports</Nav.Link>
              </Nav> 
                <Nav.Link as={NavLink} to="/BookMark" href="/BookMark" activeStyle={{Color:"white"}}>
                <div style={{color:'whitesmoke', marginRight:"1.5%"}}>
                  <FaRegBookmark data-tip="Bookmark" data-place="bottom" onClick={() => this.HandleBookmark()} />
                  <ReactTooltip/>
                </div>
                </Nav.Link>
                  {
                    this.displaySwitch()
                  }
            </Navbar.Collapse>
          </Navbar>
        )

    }
}

export default withRouter(NavigationBar)
