import React from "react"
import { withRouter } from "react-router-dom"
import Spinner from "./Spinner"
import ShareModal from "./ShareModal"
import {Card} from "react-bootstrap"
import Container from "react-bootstrap/Container"
import Col from "react-bootstrap/Col"
import Row from "react-bootstrap/Row"
import { Badge } from 'react-bootstrap'

class SearchResults extends React.Component {
    constructor() {
        super()
        this.state = {
            Loading: true,
            articlesList: [],
            artList: [],
            articleId: ""

        }
    }

    componentDidMount() {
        this.setState({articleId: window.location.search.substring(4,window.location.search.length)})
        fetch("http://localhost:5000/guardiansearch?id="+this.state.articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
         this.setState({articlesList:articles, Loading:false })
        })

        fetch("http://localhost:5000/NYtimessearch?id="+this.state.articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
         this.setState({artList: articles, Loading:false })
        })
      }

      rerender(nextProps)
    {
        this.setState({articleId: nextProps.location.search.substring(4,nextProps.location.search.length)})
        fetch("http://localhost:5000/guardiansearch?id="+this.state.articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
         this.setState({articlesList:articles, Loading:false })
        })

        fetch("http://localhost:5000/NYtimessearch?id="+this.state.articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
         this.setState({artList: articles, Loading:false })
        })
    }
    
    componentWillReceiveProps(nextProps,nextContext)
    {
        var query = nextProps.location.search.substring(4,nextProps.location.search.length);
        if(query!=this.state.articleId)
        {
            this.rerender(nextProps);
        }
    }
     
      Clicker(event,val) {
        this.props.history.push({pathname:"/DetailedArticle",search:"id=" + val})
       }

      displayItem(props) {
        return(
            <Col style={{padding:"0.5%"}}>
            <Card onClick={(event) => this.Clicker(event,props.id)} style={{boxShadow:'0px 0px 12px #333',marginTop:'2%',width:'90%',cursor:'pointer'}}>
            <Card.Title style={{fontSize:"100%",fontStyle:'italic',paddingTop:"2%", paddingLeft:"6%",paddingRight:"6%"}}>
                  {props.title} <span onClick={this.handleClick}><ShareModal title={props.title} url={props.url} /> </span>
            </Card.Title>
            <Card.Body style={{marginTop:'-1.75%'}}>
                <Card.Img src={props.imageURL} alt="" height="100%" width="100%" style={{objectFit:'cover',padding:
                    '1%', border:'solid 0.5px lightgray',float:'left', marginTop:"-4%"}}/>
                <span style={{float:'left', fontStyle:'italic'}}>{props.date}</span>
                <Card.Text style={{marginTop:"5%"}}>
                <span style={{float:'right'}}> {HandleBadge(props.sectionId)} </span>
                </Card.Text>
                </Card.Body>
                </Card>
             </Col>

        )

    }

    render()
    { 
        if(this.state.articlesList.length > 0){
            console.log(this.state.articlesList)
            console.log(this.state.artList)
            return (
                <div>
                <div style={{float:'left',fontSize:"190%",marginLeft:"2%"}}> Results </div>
                <div style={{clear:'both'}}>
                <Container fluid={true} style={{marginLeft:"0.8%"}}>
                <Row xl={4} xs={1} lg={4} md={4}>
                {this.state.articlesList.map(item => this.displayItem(item))}
                {this.state.artList.map(item => this.displayItem(item))}
                </Row>
                </Container>
                </div>
                </div>
            )
        }
        else{
            return(<Spinner />)
        }

    }
}

function HandleBadge(secname) {
    let color = ""
    let backColor = ""
    console.log(secname)

    if (secname === "world"){
        color = "#7C4EFF"
        backColor = "white"
        secname = "WORLD"
     }
    else if (secname === "politics") {
        color = "#419488"
        backColor = "white"
        secname = "POLITICS"
    }
    else if (secname === "business") {
        color = "#8EBFF3"
        backColor = "white"
        secname = "BUSINESS"
    }

    else if (secname === "technology") {
        color = "#CCCC00"
        backColor  = "black"
        secname = "TECHNOLOGY"
    }

    else if (secname === "sport" || secname=== "sports") {
        color = "#FF9930"
        backColor = "black"
        secname = "SPORTS"

    }
    else {
        color = "#778899"
        backColor = "white"
        secname = secname.toUpperCase()
     }
    

    return(
        <Badge style={{color:backColor, backgroundColor:color}}> {secname} </Badge>
    )

}

export default withRouter(SearchResults)
