import React from "react"
import Switch from "react-switch"

class ToggleSwitch extends React.Component {
    constructor() {
        super();
        this.state = { checked: this.readLocalStorage() };
        this.handleChange = this.handleChange.bind(this);
      }

      readLocalStorage(){
        if (localStorage.getItem('selected')) {
          return localStorage.getItem('selected');
        } else {
          localStorage.setItem('selected', 'guardian');
          return localStorage.getItem('selected');
        }
    }
     
      handleChange(checked) {
        console.log(checked)
        this.setState({ checked })
        console.log('Inside Handle' + this.state.checked)
        if(checked){
          this.props.updateSelectedSwitch('guardian')
        }
        else{
          this.props.updateSelectedSwitch('NYtimes')
         
        }
      }
     
      render() {
        console.log(this.state.checked)
        return (
          <React.Fragment>
            <Switch
             onChange={this.handleChange(this.state.checked)} 
             checked={this.state.checked=="guardian"} 
             checkedIcon={false} 
             uncheckedIcon = {false}
             height = {20}
             width = {40}
             onColor= "#2693e6"/>
          </React.Fragment>
        );
      }
    }

export default ToggleSwitch
