import React from "react"
import { withRouter } from "react-router-dom"
import Spinner from "./Spinner"
import ShareModal from "./ShareModal"
import {Card} from "react-bootstrap"
import Container from "react-bootstrap/Container"
import Col from "react-bootstrap/Col"
import Row from "react-bootstrap/Row"
import { left } from "glamor"
import { Badge } from 'react-bootstrap'
import {MdDelete} from "react-icons/md"
import {toast} from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import { css } from "glamor"

toast.configure()
class BookMark extends React.Component {
    constructor(){
        super()
        this.state = {
            Loading: true,
            articlesList: []
        }
     
    }
    componentDidMount() {
        var result = JSON.parse(localStorage.getItem('articles'))
        this.setState({articlesList:result,Loading:false})
    }

    handleClick(event) {
        event.stopPropagation()
        event.preventDefault()
    }

    DeleteBookMark(vals) {
        var idvalue = vals.id
        var temp = []
        var res = JSON.parse(localStorage.getItem("articles"))
        console.log(res)
        for(var i=0; i<res.length; i++){
            if (res[i].id != idvalue) {
                temp.push(res[i])
          }
         }
         localStorage.setItem("articles", JSON.stringify(temp))
         toast("Removing" + " " + vals.title, {position:toast.POSITION.TOP_CENTER, hideProgressBar:true, className:css({color:"black"})})
    }
    displayItem(props) {
        return(
            <div style={{marginBottom:'2%', marginLeft:"2%"}}>
            <Container fluid={true} style={{width:'100%'}}>
            <Row xl={3} xs={12} lg={3} md={3}>
            <Card style={{boxShadow:'0px 0px 12px #333',marginTop:'2%',width:'105%',cursor:'pointer'}}>
            <Card.Title style={{fontSize:"100%",fontStyle:'italic',paddingTop:"2%", paddingLeft:"6%",paddingRight:"6%"}}>
                  {props.title} <span onClick={this.handleClick}><ShareModal title={props.title} url={props.url} /> </span>
                  <span> <MdDelete onClick={() => this.DeleteBookMark(props)}> </MdDelete> </span>
            </Card.Title>
            <Card.Body style={{marginTop:'-1.75%'}}>
                <Card.Img src={props.imageURL} alt="" height="100%" width="100%" style={{objectFit:'cover',padding:
                    '1%', border:'solid 0.5px lightgray',float:'left', marginTop:"-4%"}}/>
                <span style={{float:'left', fontStyle:'italic'}}>{props.date}</span>
                <Card.Text style={{marginTop:"3%"}}>
                <span style={{float:'right'}}> {HandleBadge(props.sectionId)} {HandleBadge(props.source)} </span>
                </Card.Text>
                </Card.Body>
                </Card>
                </Row>
                </Container>
              </div>

        )

    }
    render()
    {
     if(this.state.Loading == false){
       if(this.state.articlesList != null){
            return (
                <div>
                <p style={{float:'left',fontSize:"190%",marginLeft:"4%"}}> Favorites</p>
                {this.state.articlesList.map(item => this.displayItem(item))}
                </div>
            )
        }     
       else{
           return(<p style={{textAlign:'center', fontSize:'98%'}}> You have no saved articles</p>)
       }
    }
    else {
        return(<Spinner />)
    }
 }
}


function HandleBadge(secname) {
    let color = ""
    let backColor = ""

    if (secname === "world"){
        color = "#7C4EFF"
        backColor = "white"
        secname = "WORLD"
     }
    else if (secname === "politics") {
        color = "#419488"
        backColor = "white"
        secname = "POLITICS"
    }
    else if (secname === "business") {
        color = "#8EBFF3"
        backColor = "white"
        secname = "BUSINESS"
    }

    else if (secname === "technology") {
        color = "#CCCC00"
        backColor  = "black"
        secname = "TECHNOLOGY"
    }

    else if (secname === "sport" || secname=== "sports") {
        color = "#FF9930"
        backColor = "black"
        secname = "SPORTS"

    }
    else if (secname === 'GUARDIAN') {
        color = '#1A2D4E'
        backColor = 'white'
        secname = 'GUARDIAN'
    }
    else if (secname === 'NYTIMES'){
        color = '#A1A1A1'
        backColor = 'black'
        secname = 'NYTIMES'
    }
    else {
        color = "#778899"
        backColor = "white"
        secname = secname.toUpperCase()
     }
    

    return(
        <Badge style={{color:backColor, backgroundColor:color}}> {secname} </Badge>
    )

}





export default withRouter(BookMark)
