import React from "react"
import { withRouter } from "react-router-dom"
import {Card} from "react-bootstrap"
import TextTruncate from "react-text-truncate"
import Container from "react-bootstrap/Container"
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"
import Badge from "react-bootstrap/Badge"

class Home extends React.Component {
     constructor() {
        super()
        this.state = {
            Loading :true,
            article_list: []
        }
    }
    componentDidMount() {
        fetch("http://localhost:5000/guardianhome").then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
            this.setState({ article_list: articles, loading:false })
        })
        

    }
    render() {
        if(this.state.article_list.length>0){
        return(
            this.state.article_list.map(item => displayItem(item))
        )}
        else{
            return <h1>loading</h1>
        }
    }
}
    function HandleBadge(secname) {
        let color = ""
        let text = ""

        if (secname === "world"){
            color = "#7B68EE"
            text = "white"
            secname = "WORLD"
         }
        else if (secname === "politics") {
            color = "#00994C"
            text = "white"
            secname = "POLITICS"
        }
        else if (secname === "business") {
            color = "#0080FF"
            text = "white"
            secname = "BUSINESS"
        }

        else if (secname === "technology") {
            color = "#CCCC00"
            text  = "black"
            secname = "TECHNOLOGY"
        }

        else if (secname === "sport" || secname=== "sports") {
            color = "#FF9930"
            text = "black"
            secname = "SPORTS"

        }
        else {
            color = "#778899"
            text = "white"
            secname = secname.toUpperCase()
         }
        return(
            <Badge variant={color}>
                   {secname}
            </Badge>
        )

    }

function displayItem (props) {
    return(
        <div style={{marginBottom:'20px'}}>
        <Container fluid={true} style={{width:'95%'}}>
        <Row xs={1} md={1} lg={1} xl={1}>
        <Card style={{boxShadow:'0px 0px 25px #333', marginTop:'2%',width:'98%',cursor:'pointer'}}>
        <Card.Body>
            <Col xl={3} xs={12} lg={3} md={3} style={{float:'left'}}>
            <div style={{padding:
            '3px', border:'solid 0.5px lightgray'}}>
            <Card.Img src={props.imageURL} alt="" height="auto" width="auto" style={{objectFit:'cover'}}/>
            </div>
            </Col>
            <Col xl={9} xs={12} style={{float:'right'}}>
                <Card.Body style={{marginTop:'-1.75%'}}>
                    <Card.Title style={{fontweight:'bold',fontStyle:'italic'}}>
                    {props.title}
                    </Card.Title>
                    <TextTruncate line={3} text={props.description}/>
                    <Card.Text style={{marginTop:"2%"}}>
                        <span style={{fontWeight:'550',fontStyle:'italic'}}>{props.date}</span>
                        <span style={{marginLeft:'625px'}}>{HandleBadge(props.sectionId)}</span>
                    </Card.Text>
                </Card.Body>
             </Col>
        </Card.Body>
         </Card>
         </Row>
        </Container>
      </div>
    )

}

export default withRouter(Home)
