import React from  "react"
import {EmailShareButton,
    FacebookShareButton, TwitterShareButton} from "react-share"
import {EmailIcon,FacebookIcon,TwitterIcon} from "react-share"
import Modal from 'react-bootstrap/Modal'

class Share extends React.Component {
    constructor(props){
        super(props)
    }
   render() {
        console.log(this.props.url)
    return (
        <Modal show={this.props.select} onHide={this.props.hide}>
        <Modal.Header closeButton>
            <Modal.Title>{this.props.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
              <p style={{textAlign:'center', fontWeight:'bold',fontSize:'25px'}}> Share via </p>
              <FacebookShareButton url={this.props.url} hashtag={"CSCI_571_NewsApp"}><FacebookIcon size={15} round={true}></FacebookIcon></FacebookShareButton>
              <TwitterShareButton url={this.props.url}><TwitterIcon></TwitterIcon></TwitterShareButton>
              <EmailShareButton url={this.props.url}><EmailIcon></EmailIcon></EmailShareButton>
              </Modal.Body>
        </Modal>
     
    );
  }
}
  


export default Share
