import React from 'react'
import './App.css'
import NavigationBar from "./NavigationBar"
import 'bootstrap/dist/css/bootstrap.min.css'
import Home from "./Home"
import World from "./World"
import Politics from "./Politics"
import Business from "./Business"
import Technology from "./Technology"
import Sports from "./Sports"
import DetailedArticle from "./DetailedArticle"
import { BrowserRouter, 
  Route, 
  Switch } from 'react-router-dom';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state= {
      checked: true,
      hide: false
    }
    this.hideSwitch = this.hideSwitch.bind(this);
    this.handleCheck = this.handleCheck.bind(this);
  }

  hideSwitch=(value) => {
    console.log(value);
    this.setState({hide:value})
  }
  handleCheck = (value) => {

    this.setState({checked:value});
  }

  render() {
  return (
    <div className="App">
      <BrowserRouter>
          <NavigationBar checkValue = {this.handleCheck} hide={this.state.hide}/>
          <Switch>
            <Route exact path="/" ><Home vals={this.hideSwitch}/></Route>
            <Route exact path="/World"> <World /> </Route>
            <Route exact path="/Politics"> <Politics /> </Route>
            <Route exact path="/Business"> <Business /> </Route>
            <Route exact path="/Technology"> <Technology /> </Route>
            <Route exact path="/Sports"> <Sports /> </Route>
            <Route exact path="/DetailedArticle"><DetailedArticle /></Route>
            <Route exact path= "/BookMark" vals={this.hideSwitch}></Route>
          </Switch>
      </BrowserRouter>
    </div>
  );
  }
}

export default App;
