import React from "react"
import { withRouter } from "react-router-dom"

class Home extends React.Component {
     constructor() {
        super()
        this.state = {
            Loading : true,
            article_list: ""
        }
    }
    componentDidMount() {
        fetch("http://localhost:5000/guardianhome").then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
            this.setState({ article_list: articles })
        })
        

    }
    render() {
        return(
            <h1>{this.state.article_list[0]}</h1>
        )
    }
}

export default withRouter(Home)
