import React from "react"
import { withRouter } from "react-router-dom"
import {Card} from "react-bootstrap"
import Container from "react-bootstrap/Container"
import Spinner from "./Spinner"
import {EmailShareButton,
    FacebookShareButton, TwitterShareButton} from "react-share"
import {EmailIcon,FacebookIcon,TwitterIcon} from "react-share"
import {FaRegBookmark} from "react-icons/fa"
import CommentBox from "./CommentBox"
import {FaAngleDown, FaAngleUp} from "react-icons/fa"
import ReactTooltip from 'react-tooltip'

class DetailedArticle extends React.Component {
    constructor(){
        super()
    this.state = {
        Loading: true,
        article: [],
        init: true,
        fullContent:false
        
    }
    this.myRef = React.createRef();
}

toggleContent = () => {
this.setState({
fullContent: !this.state.fullContent
}, this.animateArticle);
}

animateArticle = () => {
if(this.state.fullContent === false){
window.scrollTo({bottom:1000,behavior: 'smooth' });
}

if(this.state.fullContent === true){
window.scrollTo({top: this.myRef.current.offsetTop, behavior: 'smooth'});
}
}



   
    componentDidMount() {
        if (localStorage.getItem('selected') === "guardian") {
        let articleId = window.location.search.substring(4,window.location.search.length)
        fetch("http://localhost:5000/guardiandetailed?id="+articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })
    }
       else{
        let articleId = window.location.search.substring(4,window.location.search.length)
        fetch("http://localhost:5000/NYtimesdetailed?vals="+articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })
       }
    }

     Splitter(props) {
         let desc = props.description.split(".")
         let sliceval1 =  desc.slice(0,3).join(".")+"."
         let sliceval2 = desc.slice(3,).join(".") + "."
         if (this.state.init) {
            
         return(
             <div>
             <div> {sliceval1} </div>
             <FaAngleDown onClick={() => {this.setState({init:false});this.Splitter(props)}} size={22} style={{float:"right"}}/>
             </div>
         )
     }
         else{
             return(
                 <div>
                 <div ref={this.myRef}> {sliceval1+sliceval2} </div>
                 <FaAngleUp onClick={() => {this.setState({init:true});this.Splitter(props);this.toggleContent()}} size={22} style={{float:"right"}}/>
                 </div>
             )

         }

     }
        
        displayItem(props) {
            return(
                <div style={{marginBottom:'2%', marginLeft:"2%"}}>
                <Container fluid={true} style={{width:'100%'}}>
                <Card style={{boxShadow:'0px 0px 12px #333', marginTop:'2%',width:'98%',cursor:'pointer'}}>
               <Card.Title style={{fontSize:"200%",fontStyle:'italic',paddingTop:"2%", paddingLeft:"2%",paddingRight:"2%"}}>
                  {props.title} 
                </Card.Title>
                <Card.Body style={{marginTop:'-1.75%'}}>
                <span style={{fontWeight:'550',fontStyle:'italic',marginTop:"-5%",marginLeft:"0.5%"}}>
                    {props.date}
                <span style={{marginLeft:"75%"}}>
                 <FacebookShareButton data-tip="Facebook" url={props.url} hashtag={"#CSCI_571_NewsApp"}><FacebookIcon size={30} round={true}></FacebookIcon></FacebookShareButton>
                  <TwitterShareButton data-tip="Twitter" url={props.url}><TwitterIcon size={30} round={true}></TwitterIcon></TwitterShareButton>
                 <EmailShareButton data-tip="Email" url={props.url} subject={"#CSCI_571_NewsApp"}><EmailIcon size={30} round={true}></EmailIcon></EmailShareButton>
                 <ReactTooltip />
                <span style={{marginLeft:"5%", color:"red"}}> <FaRegBookmark /> </span>
                </span>
                </span>
                <Card.Img src={props.imageURL} alt="" height="100%" width="100%" style={{objectFit:'cover',padding:
                    '0.2%', border:'solid 0.5px lightgray',float:'left', marginTop:"0.5%"}}/>
                <span> {this.Splitter(props)} </span>
                </Card.Body>
                </Card>
                <CommentBox vals={props.id}/>
                </Container>
              </div>
            )
        
        }
        


    render() {
        if (this.state.article.length > 0) {
           return( this.state.article.map((item) => this.displayItem(item)))
        }
        else{
            return(<Spinner />)
        }
            
        
    }
}

export default withRouter(DetailedArticle)
