import React from "react"
import { withRouter } from "react-router-dom"
import Spinner from "./Spinner"
import ShareModal from "/ShareModal"
import {Card} from "react-bootstrap"
import Container from "react-bootstrap/Container"

class BookMark extends React.Component {
    constructor(){
        super()
        this.state = {
            Loading: true,
            articlesList: []
        }
     
    }
    componentDidMount() {
        var result = JSON.parse(localStorage.getItem('articles'))
        this.setState({articlesList:result,Loadin:false})
    }
    
    displayItem(props) {
        return(
            <div style={{marginBottom:'2%', marginLeft:"2%"}}>
            <Container fluid={true} style={{width:'100%'}}>
            <Card style={{marginTop:'2%',width:'98%',cursor:'pointer'}}>
            <Card.Title style={{fontSize:"200%",fontStyle:'italic',paddingTop:"2%", paddingLeft:"2%",paddingRight:"2%"}}>
                  {props.title} 
            </Card.Title>
            <Card.Body style={{marginTop:'-1.75%'}}>
            <span style={{fontWeight:'550',fontStyle:'italic',marginTop:"-5%",marginLeft:"0.5%"}}>
                    {props.date}
                <span style={{marginLeft:"75%"}}>
                </span>
                </span>
                <Card.Img src={props.imageURL} alt="" height="100%" width="100%" style={{objectFit:'cover',padding:
                    '0.2%', border:'solid 0.5px lightgray',float:'left', marginTop:"0.5%"}}/>
                <span> {this.Splitter(props)} </span>
                </Card.Body>
                </Card>
                </Container>
              </div>

        )

    }
    render()
    {
       if(this.state.articlesList.length > 0){
            return (this.state.articlesList.map(item => this.displayItem(item)))
        }
       else{
           return(<Spinner />)
       }
    }
}











export default withRouter(BookMark)
