import React from "react"
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import AsyncSelect from 'react-select/async'
import {FaRegBookmark} from 'react-icons/fa'
import ToggleSwitch from './ToggleSwitch'
import {withRouter} from "react-router-dom"
import {Link, NavLink} from "react-router-dom"
import { NavItem } from "react-bootstrap"
import ReactTooltip from "react-tooltip"
import {FaBookmark} from "react-icons/fa"
import _ from "lodash"
class NavigationBar extends React.Component {
   constructor(){
     super()
     this.state = {
       selected : true,
       hidden: false,
       filled: false,
       searchvalue: ""
     }
     this.handleCheck = this.handleCheck.bind(this);
     this.updateSelectedSwitch = this.updateSelectedSwitch.bind(this)
   }

   handleCheck(check){
     this.props.checkValue(check);
   }
   displaySwitch(){
     console.log("hide:", this.props.hide);
     if(localStorage.getItem('hide') === "false"){
       return (
        <React.Fragment>
        <NavItem style={{marginRight:'1.5%',color:"whitesmoke"}}>NYTimes</NavItem>
        <ToggleSwitch updateSelectedSwitch={this.updateSelectedSwitch}/>
        <NavItem style={{marginLeft:'1.5%',color:"whitesmoke"}}>Guardian</NavItem>
        </React.Fragment>
       )
     }
   }
   

   updateSelectedSwitch(val){
     console.log(val)
     this.setState({selected: val})
     localStorage.setItem('selected',val)
     this.handleCheck(val);
    }

    HandleBookmark() {
      localStorage.setItem('hide', true);
      this.setState({filled:true})
      this.props.history.push({pathname:"/BookMark"})
    }
    handleSearchChange = async (event, { value }) => {
      try {
        const response = await fetch(
          `https://sumukh.cognitiveservices.azure.com/bing/v7.0/suggestions?q=${value}`,
          {
            headers: {
              "Ocp-Apim-Subscription-Key": "e075690357ef4b5ea9f84303a5e99939"
            }
          }
        );
        const data = await response.json();
        const resultsRaw = data.suggestionGroups[0].searchSuggestions;
        const results = resultsRaw.map(result => ({ title: result.displayText, url: result.url }));
        this.setState({ results });
      } catch (error) {
        console.error(`Error fetching search ${value}`);
      }
    }

    Searcher(searchval) {
      this.setState({searchvalue:searchval})
      this.props.history.push({pathname:"/SearchResults", search:"val=" + searchval.title})
    }
    render(){
      var filledbookmark = ""
      if(this.state.filled == false) {
        filledbookmark = <div style={{color:'whitesmoke', marginRight:"1.5%"}}>
        <FaRegBookmark data-tip="Bookmark" data-place="bottom" onClick={() => this.HandleBookmark()} />
        <ReactTooltip/>
      </div>
     }
     else {
      filledbookmark = <div style={{color:'whitesmoke', marginRight:"1.5%"}}>
      <FaBookmark data-tip="Bookmark" data-place="bottom" />
      <ReactTooltip/>
    </div>

     }
        return(
            <Navbar collapseOnSelect bg='dark' expand="lg" variant="dark" className="nav-name">
            <AsyncSelect className="nav-search"
              cacheOptions={true}
              LoadOptions = {_.debounce(this.handleSearchChange, 1000, {
              leading: true
              })}
              placeholder= "Enter keyword .." onChange={this.Searcher}/>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto" defaultActiveKey="/">
                <Nav.Link as={NavLink} exact to="/" onClick={()=> {this.setState({hidden:false,filled:false}); localStorage.setItem('hide', false);}} href="/" activeStyle={{color:"white"}} style={{color:"lightgray"}}>Home</Nav.Link>
                <Nav.Link as={NavLink} to="/World" onClick={()=> {this.setState({hidden:false,filled:false})}} href="/World" activeStyle={{color:"white"}} style={{color:"lightgray"}}>World</Nav.Link>
                <Nav.Link as={NavLink} to="/Politics" href="/Politics" onClick={()=> {this.setState({hidden:false,filled:false})}} activeStyle={{color:"white"}} style={{color:"lightgray"}}>Politics</Nav.Link>
                <Nav.Link as={NavLink} to="/Business" href="/Business" onClick={()=> {this.setState({hidden:false,filled:false})}} activeStyle={{color:"white"}} style={{color:"lightgray"}}>Business</Nav.Link>
                <Nav.Link as={NavLink} to="/Technology" href="/Technology"onClick={()=> {this.setState({hidden:false,filled:false})}} activeStyle={{color:"white"}} style={{color:"lightgray"}}>Technology</Nav.Link>
                <Nav.Link as={NavLink} to="/Sports" href="/Sports" onClick={()=> {this.setState({hidden:false,filled:false})}}activeStyle={{color:"white"}} style={{color:"lightgray"}}>Sports</Nav.Link>
              </Nav> 
                <Nav.Link as={NavLink} to="/BookMark" href="/BookMark" activeStyle={{Color:"white"}}>
                      {filledbookmark}
                </Nav.Link>
                  {
                    this.displaySwitch()
                  }
            </Navbar.Collapse>
          </Navbar>
        )

    }
}

export default withRouter(NavigationBar)
