import React from "react"
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import AsyncSelect from 'react-select/async'
import {FaRegBookmark} from 'react-icons/fa'
import ToggleSwitch from './ToggleSwitch'
import {withRouter} from "react-router-dom"
import {Link} from "react-router-dom"
class NavigationBar extends React.Component {
   constructor(){
     super()
     this.state = {
       selected : this.readLocalStorage()
     }
     this.handleCheck = this.handleCheck.bind(this);
     this.updateSelectedSwitch = this.updateSelectedSwitch.bind(this)
   }

   handleCheck(check){
     this.props.checkValue(check);
   }
   

   readLocalStorage(){
    if(typeof(Storage) !== "undefined") {
      if (localStorage.selected) {
        return localStorage.selected;
      } else {
        localStorage.selected = 'guardian';
        return localStorage.selected;
      }
   }
  }

   updateSelectedSwitch(val){
     console.log("in state")
     this.setState({selected: val})
     localStorage.setItem('selected',val)
     this.handleCheck(val);
    }
  
    render(){
        return(
            <Navbar collapseOnSelect bg='dark' expand="lg" variant="dark" className="nav-name">
            <AsyncSelect className="nav-search"
              cacheOptions
              defaultOptions
              placeholder= "Enter keyword .."/>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto" defaultActiveKey="/">
                <Nav.Link as={Link} to="/" href="/" >Home</Nav.Link>
                <Nav.Link as={Link} to="/World" href="/World">World</Nav.Link>
                <Nav.Link as={Link} to="/Politics" href="/Politics">Politics</Nav.Link>
                <Nav.Link as={Link} to="/Business" href="/Business">Business</Nav.Link>
                <Nav.Link as={Link} to="/Technology" href="/Technology">Technology</Nav.Link>
                <Nav.Link as={Link} to="/Sports" href="/Sports">Sports</Nav.Link>
              </Nav>
                <div style={{display:'inline-block', color:'whitesmoke'}}>
                <Nav>
                  <span style={{marginLeft:40,marginRight:10,marginTop:10}}><Nav><FaRegBookmark /></Nav></span>
                  <span style={{marginLeft:10,marginRight:15,marginTop:5}}><Nav>NYTimes</Nav></span>
                  <span style={{marginleft:10,marginRight:15,marginTop:8}}><Nav> <ToggleSwitch updateSelectedSwitch={this.updateSelectedSwitch}/> </Nav> </span>
                  <span style={{marginleft:10,marginRight:15,marginTop:5}}><Nav>Guardian</Nav> </span>
                </Nav>
                </div>
            </Navbar.Collapse>
          </Navbar>
        )

    }
}

export default withRouter(NavigationBar)
