import React from "react"
import { withRouter } from "react-router-dom"

class Home extends React.Component {
     constructor() {
        super()
        this.state = {
            Loading : true,
            article_list: []
        }
    }
    componentDidMount() {
        fetch("http://localhost:5000/guardianhome").then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
            this.setState({ article_list: articles })
        })
        

    }
    render() {
        if(this.state.article_list.length>0){
        return(
              displayItem(this.state.article_list[0])
        )}
        else{
            return <h1>loading</h1>
        }
    }
}

function displayItem (props) {
    return(
        <div>
            <img src={props.imageURL} alt="" style={{width:20,height:20}}/>
            <p>{props.title} </p>
            <p>{props.description}</p>
        </div>
    )

}

export default withRouter(Home)
