import React from "react"
import { withRouter } from "react-router-dom"
import ShareModal from "./ShareModal"
import {Card} from "react-bootstrap"
import TextTruncate from "react-text-truncate"
import Container from "react-bootstrap/Container"
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"
import Spinner from "./Spinner"

class DetailedArticle extends React.Component {
    constructor(){
        super()
    this.state = {
        Loading: true,
        article: []
    }
}
   
    componentDidMount() {
        if (localStorage.getItem('selected') === "guardian") {
        let articleId = window.location.search.substring(4,window.location.search.length)
        fetch("http://localhost:5000/guardiandetailed?id="+articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })
    }
       else{
        let articleId = window.location.search.substring(4,window.location.search.length)
        fetch("http://localhost:5000/NYtimesdetailed?vals="+articleId).then(response => response.json())
        .then(response => {
            const articles = response.result
         this.setState({ article: articles, Loading:false })
        })
       }
    }
        
        displayItem(props) {
            return(
                <div style={{marginBottom:'2%'}}>
                <Container fluid={true} style={{width:'100%'}}>
                <Card style={{boxShadow:'0px 0px 12px #333', marginTop:'2%',width:'98%',cursor:'pointer'}}>
                <Card.Body>
                <Card.Title style={{fontSize:"200%",fontStyle:'italic'}}>
                  {props.title} 
                </Card.Title>
                    <Card.Img src={props.imageURL} alt="" height="auto" width="auto" style={{objectFit:'cover',padding:
                    '0.2%', border:'solid 0.5px lightgray',float:'left'}}/>
                        <Card.Body style={{marginTop:'-1.75%'}}>
                            <TextTruncate line={3} text={props.description}/>
                            <Card.Text style={{marginTop:"2%"}}>
                                <span style={{fontWeight:'550',fontStyle:'italic'}}>{props.date}</span>
                            </Card.Text>
                        </Card.Body>
                </Card.Body>
                 </Card>
                </Container>
              </div>
            )
        
        }
        


    
    render() {
        if (this.state.article.length > 0) {
           return( this.state.article.map((item) => this.displayItem(item)))
        }
        else{
            return(<Spinner />)
        }
            
        
    }
}

export default withRouter(DetailedArticle)
