import React from "react"
import { withRouter } from "react-router-dom"
import {Card} from "react-bootstrap"
import TextTruncate from "react-text-truncate"
import Container from "react-bootstrap/Container"
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"
import { Badge } from 'react-bootstrap'
import Spinner from "./Spinner"
import ShareModal from "./ShareModal"


class Home extends React.Component {
     constructor() {
        super()
        this.state = {
            Loading :true,
            article_list: [],
            art_list: [],
            clicked: false
        }
        this.Clicker = this.Clicker.bind(this)
    }


    componentDidMount() {
        console.log('HOME :');
        fetch("http://localhost:5000/guardianhome").then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
            this.setState({ article_list: articles, Loading:false })
        })
    
   
        fetch("http://localhost:5000/NYtimeshome").then(response => response.json())
        .then(response => {
            const articles = response.result
            console.log(articles)
            this.setState({ art_list: articles, Loading:false })
        })

    }
        
    Clicker(event,val) {
        if (localStorage.getItem('selected') === "guardian") {
            this.props.history.push({pathname:"/DetailedArticle",search:"id=" + val})
        }
        else{
            this.props.history.push({pathname:"/DetailedArticle",search:"vals=" + val})
        }
    
    }

    displayItem (props) {
        return(
            <div style={{marginBottom:'20px'}}>
            <Container fluid={true} style={{width:'95%'}}>
            <Row xs={1} md={1} lg={1} xl={1}>
            <Card onClick={(event) => this.Clicker(event,props.id)} style={{boxShadow:'0px 0px 12px #333', marginTop:'2%',width:'98%',cursor:'pointer'}}>
            <Card.Body>
                <Col xl={3} xs={12} lg={3} md={3} style={{float:'left'}}>
                <div style={{padding:
                '3px', border:'solid 0.5px lightgray'}}>
                <Card.Img src={props.imageURL} alt="" height="auto" width="auto" style={{objectFit:'cover'}}/>
                </div>
                </Col>
                <Col xl={9} xs={12} style={{float:'right'}}>
                    <Card.Body style={{marginTop:'-1.75%'}}>
                        <Card.Title style={{fontweight:'bold',fontStyle:'italic'}}>
                        {props.title} <ShareModal title={props.title} url={props.url} />
                        </Card.Title>
                        <TextTruncate line={3} text={props.description}/>
                        <Card.Text style={{marginTop:"2%"}}>
                            <span style={{fontWeight:'550',fontStyle:'italic'}}>{props.date}</span>
                            <span style={{float:'right'}}>{HandleBadge(props.sectionId)}</span>
                        </Card.Text>
                    </Card.Body>
                 </Col>
            </Card.Body>
             </Card>
             </Row>
            </Container>
          </div>
        )
    
    }
    render() {
    console.log("Source ", localStorage.getItem('selected'));
    if(this.state.article_list.length>0){
            if (localStorage.getItem('selected').toString() === 'guardian') {
                return(
                        this.state.article_list.map(item => this.displayItem(item))
                        )
                    }
            else {
                    return(
                        this.state.art_list.map(item => this.displayItem(item))
                    )
                }
            }
    else{
        return(
            <Spinner />)
        }
    }
}


    function HandleBadge(secname) {
        let color = ""
        let backColor = ""

        if (secname === "world"){
            color = "#7C4EFF"
            backColor = "white"
            secname = "WORLD"
         }
        else if (secname === "politics") {
            color = "#419488"
            backColor = "white"
            secname = "POLITICS"
        }
        else if (secname === "business") {
            color = "#8EBFF3"
            backColor = "white"
            secname = "BUSINESS"
        }

        else if (secname === "technology") {
            color = "#CCCC00"
            backColor  = "black"
            secname = "TECHNOLOGY"
        }

        else if (secname === "sport" || secname=== "sports") {
            color = "#FF9930"
            backColor = "black"
            secname = "SPORTS"

        }
        else {
            color = "#778899"
            backColor = "white"
            secname = secname.toUpperCase()
         }

        return(
            <Badge style={{color:backColor, backgroundColor:color}}> {secname} </Badge>
        )

    }



export default withRouter(Home)
